import React, { useEffect } from "react";
import axios from "axios";

function PatientPanel() {
  const [patientName, setPatientName] = React.useState("");
  const [treatID, setTreatID] = React.useState("");
  const [category, setCategory] = React.useState("");

  function fetchPatientRecords() {
    axios.get("http://localhost:8080/patients").then((response) => {
      // handle success
      var resData = response.data;
      let data = JSON.parse(resData);
      window.alert("Response recieved from server = " + data);
      return data.map((datas) => (
        <li>
          {datas.name}, {datas.treatID}, {datas.category}
        </li>
      ));
    });
  }

  // function showPatientRecords() {
  //   axios.get("http://localhost:8080/patients").then((response) => {
  //     var resData = response.data;
  //     let data = JSON.parse(resData);
  //     const showRecords = data.map((datas) => (
  //       <li>
  //         {datas.name},{datas.treatID},{datas.category}
  //       </li>
  //     ));
  //     //   return showRecords();
  //   });
  // }

  function savePatient() {
    const value = {
      name: patientName,
      treatID: treatID,
      category: category,
    };

    axios.post("http://localhost:8080/patient", value).then((response) => {
      // handle success
      var resData = response.data;
      let data = JSON.stringify(resData);
      window.alert("Response recieved from server = " + data);
    });
  }

  function deletePatient() {
    axios
      .delete(`http://localhost:8080/deletePatient/${patientName}`)
      .then((response) => {
        // handle success
        var resData = response.data;
        let data = JSON.stringify(resData);
        window.alert("Response recieved from server = " + data);
      });
  }

  function updatePatient() {
    const value = {
      name: patientName,
      treatID: treatID,
      category: category,
    };
    axios
      .put(`http://localhost:8080/updatePatient/${patientName}`, value)
      .then((response) => {
        // handle success
        var resData = response.data;
        let data = JSON.stringify(resData);
        window.alert("Response recieved from server = " + data);
      });
  }

  function displayPatientHandler() {
    fetchPatientRecords();
  }

  function savePatientHandler() {
    savePatient();
  }

  function deletePatientHandler() {
    deletePatient();
  }

  function updatePatientHandler() {
    updatePatient();
  }

  return (
    <div>
      <input
        type="text"
        placeholder="Patient Name"
        value={patientName}
        onChange={(e) => setPatientName(e.target.value)}
      />
      <br />
      <input
        type="text"
        placeholder="Treatment ID"
        value={treatID}
        onChange={(e) => setTreatID(e.target.value)}
      />
      <br />
      <input
        type="text"
        placeholder="Category"
        value={category}
        onChange={(e) => setCategory(e.target.value)}
      />
      <br />
      <button onClick={savePatientHandler}>Save Patient</button>
      <br />
      <button onClick={deletePatientHandler}>Delete Patient</button>
      <br />
      <button onClick={updatePatientHandler}>Update Patient</button>
      <br />
      <h3>Show Patients:</h3>
      <button onClick={displayPatientHandler}>Display Patients</button>
    </div>
  );
}

export default PatientPanel;
